import axios from "axios";

const apiClient = axios.create({
  baseURL: "http://sandbox.pavelon.com",
  withCredentials: true,
  headers: {
    Accept: "",
    "Content-type": "",
  },
});

export default {
  postLogin() {
    return apiClient.post("login");
  },
  getUsers() {
    return apiClient.get("users");
  },
  postUsers() {
    return apiClient.post("users");
  },
};
